mapper.update:
UPDATE user
SET gender = #{gender}, name = #{name}, area = #{area}, address = #{address}, tel_phone = #{telPhone}, zip_code = #{zipCode}, email = #{email}
WHERE id = #{id}

mapper.select:
SELECT user.token
FROM user
WHERE user.id = #{id}

SELECT user.token
FROM user
WHERE user.username = #{username}

mapper.update:
UPDATE user
SET token = #{token}
WHERE id = #{id}
